import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Forumexample.css'; // Import the CSS file with the provided styles

const PostComponent = ({ post }) => {
  const [likes, setLikes] = useState(0);
  const [comments, setComments] = useState([]);
  const [commentText, setCommentText] = useState('');
  const [profileImage, setProfileImage] = useState(null); // State to store the profile image URL

  useEffect(() => {
    // Fetch the profile image URL from MongoDB based on post.author
    axios.get(`${process.env.REACT_APP_API_URL}/api/users/profileImage/${post.author}`)
      .then(response => {
        setProfileImage(response.data.profileImageURL);
      })
      .catch(error => {
        console.error('Error fetching profile image:', error);
      });
  }, [post.author]);

  const handleLike = () => {
    setLikes(likes + 1);
  };

  const handleCommentSubmit = (e) => {
    e.preventDefault();
    if (commentText.trim()) {
      setComments([...comments, commentText]);
      setCommentText('');
    }
  };

  return (
    <div className="post-container"> {/* Add the "post-container" class */}
      <div className="post-header">
        <img src={profileImage || 'default-profile-image-url.jpg'} alt="Profile" className="profile-pic" /> {/* Use the fetched profile image URL or a default image */}
        <div>
          <h2 className='title'>{post.title}</h2>
          <div className='profile-info'>
            <h3 className='name'>{post.author}</h3> 
            <h3 className='location'>{post.location}</h3>
          </div>
        </div>
        <div className="tag">CULTURE</div>
      </div>
      <div className="post-body">
        <p>{post.content}</p>
      </div>
      <div className="post-footer">
        <div className="post-stats">
          Seen {post.views} times | {new Date(post.createdAt).toLocaleDateString()}
        </div>
        <div className="post-actions">
          <button onClick={handleLike}>❤️ Like it</button>
          <span>{likes} reaction{likes !== 1 ? 's' : ''}</span>
        </div>
      </div>
      <div className="post-comments">
        {comments.map((comment, index) => (
          <p key={index}>{comment}</p>
        ))}
        <form onSubmit={handleCommentSubmit}>
          <input 
            type="text" 
            placeholder="Add a comment" 
            value={commentText} 
            onChange={(e) => setCommentText(e.target.value)} 
            className="comment-input" // Remove the curly braces from the comment
          />
          <button type="submit" className="comment-button">Send</button> {/* Add the "comment-button" class */}
        </form>
      </div>
    </div>
  );
};

export default PostComponent;
