import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import PostComponent from './PostComponent'; // Make sure the path is correct
import './Forum.css';

// ... Import your assets

const Section = ({ title, children }) => {
  return (
    <section>
      <h2>{title}</h2>
      {children}
    </section>
  );
};

function Forum() {
  const [posts, setPosts] = useState([]);
  const [selectedPost, setSelectedPost] = useState(null);
  const [recommendedArticles, setRecommendedArticles] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch forum posts
    axios.get('http://localhost:3001/forum/posts')
      .then(response => {
        setPosts(response.data);
      })
      .catch(error => console.error('Error fetching posts:', error));

    // Generate recommended articles (you can modify this logic)
    const shuffled = posts.sort(() => 0.5 - Math.random()).slice(0, 3);
    setRecommendedArticles(shuffled);
  }, []); // Empty dependency array to fetch data only once

  const goToPost = (postId) => {
    const post = posts.find(p => p._id === postId);
    setSelectedPost(post);
  };

  const handlePostsClick = (articleId) => {
    // Handle clicking on recommended articles
    console.log(`Article clicked: ${articleId}`);
    navigate(`/${articleId}`);
  };

  const handleprofile = () => {
    navigate('/share');
  };

  return (
    <div className="Forum">
      <Section className="Forums">
        <div className="forum-content">
          {/* your background image */}
        </div>
      </Section>

      {selectedPost ? (
        <PostComponent post={selectedPost} />
      ) : (
        <Section title='Forum Posts'>
          <div className="posts-list">
            {posts.map((post) => (
              <div key={post._id} onClick={() => goToPost(post._id)} className="post">
                <h3 className="post-title">{post.title}</h3>
                <p className="post-details">
                  <span className="post-author">{post.author}</span> |{' '}
                  <time className="post-date">{new Date(post.createdAt).toLocaleDateString()}</time>
                </p>
              </div>
            ))}
          </div>
        </Section>
      )}

      <Section title="Recommendations for you">
        <div className="image-text-container">
          {recommendedArticles.map((article) => (
            <div className="image-block" key={article._id}>
              <img
                src={article.src} // Use the appropriate image source
                alt={article.title}
                className="image"
                onClick={() => handlePostsClick(article._id)}
              />
              <div className="text">{article.title}</div>
            </div>
          ))}
        </div>
      </Section>

      <Section>
        <div className="story-share">
          <h1 className='story-text1'>Share your story</h1>
          <p className='story-text2'>Do you want to keep track of your journey? Or do you prefer to connect with others who are also new in Finland? Please share your story and experiences here!</p>
          <button className="share-story-button" onClick={handleprofile}>Click here</button>
        </div>
      </Section>
    </div>
  );
}

export default Forum;
