import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Forumexample.css';
import culturebkg from '../assets/culturebakg.png';
import phillip from '../assets/phillipprofile.png';
import { Post } from './Post'; // Import the Post component

const Cultureexample = () => {
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  const navigate = useNavigate();
  const [likes, setLikes] = useState(0);
  const [comments, setComments] = useState([]);
  const [commentText, setCommentText] = useState('');

  const handleLike = () => {
    setLikes(likes + 1);
  };

  const handleCommentSubmit = (e) => {
    e.preventDefault();
    if (commentText.trim()) {
      setComments([...comments, commentText]);
      setCommentText('');
    }
  };

  // Define the post data
  const postData = {
    title: 'Finnish Culture',
    author: 'Phillip Johnson',
    location: 'Helsinki, Finland',
    content: `Hei kaikki!\n\nIt's Phillip here. Adapting to Finnish culture has been an exciting, but hard journey. Finns might seem quiet at first, but they're incredibly friendly once you get to know them.\n\nMy advice? Participate in local events and student organizations. There's always something happening, from music festivals to 'vappu' (May Day) celebrations. Libraries here are amazing places to study and unwind. And remember, the sauna is a significant part of Finnish culture - it's a must-try for anyone!`,
    stats: 'Seen 230 times | 11 nov. 2023',
    likes,
    comments,
    handleLike,
    handleCommentSubmit,
    commentText,
    setCommentText,
  };

  return (
    <div>
      <section className="Forums">
        <div className="forum-content">
          <img src={culturebkg} alt="culture background" />
        </div>
      </section>
      <div className="post-container">
        {/* Render the Post component with the post data */}
        <Post {...postData} />
      </div>
      {/* Rest of your component */}
    </div>
  );
};

export default Cultureexample;
