import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css'; 
import NavIcon2 from './assets/icon.jpg';

function Navbar() {
  // Assume you have a state variable to track user authentication status
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const [dropdownOpen, setDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  const handleLogout = () => {
    // Implement your logout logic here, e.g., clear user session or JWT token
    // After logging out, update the isLoggedIn state accordingly
    setIsLoggedIn(false);
  };

  return (
    <nav className="navbar">
      <div className="navbar-icons">
        <img src={NavIcon2} alt="Icon 2" /> 
      </div>
      <div className="navbar-links">
        <Link to="/" className="nav-item">Home</Link>
        <Link to="/Overview" className="nav-item">Overview</Link>
        <div className="dropdown">
          <div className='dropdown-menu'> Forums
            <div className="dropdown-content">
              <Link to="/Forum" className="dropdown-item" onClick={() => setDropdownOpen(false)}>Forum</Link>
              <Link to="/Share" className="dropdown-item" onClick={() => setDropdownOpen(false)}>Share Your Experience</Link>
            </div>
          </div>
        </div>
        {/* Display "Log in" or "Log out" based on isLoggedIn state */}
        {isLoggedIn ? (
          <button className="logout-button" onClick={handleLogout}>Log out</button>
        ) : (
          <Link to="/Login" className="login-button">Log in</Link>
        )}
        <Link to="/Profile" className="nav-item">Profile</Link>
      </div>
    </nav>
  );
}

export default Navbar;
