import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Forum.css'; 
import forumbkg from '../assets/forumbkg.png';
import recom1 from '../assets/recom1.png';
import recom2 from '../assets/recom2.png';
import recom3 from '../assets/recom3.png';
import PostComponent from './PostComponent'; // Adjust the path as necessary

const Section = ({ title, children }) => {
  return (
    <section>
      <h2>{title}</h2>
      {children}
    </section>
  );
};

function Forum() {
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate(); 

  useEffect(() => {
    axios.get('http://localhost:3001/forum/posts')
      .then(response => {
        setPosts(response.data);
      })
      .catch(error => console.error('Error fetching posts:', error));
  }, []);

  const goToPost = (postId) => {
    // Navigate to the post's detailed page, or handle as needed
    navigate(`/${postId}`);
  };

  const handleprofile = () => {
    navigate('/share'); 
  };

  return (
    <div className="Forum">
      <Section className="Forums">
        <div className="forum-content">
          <img src={forumbkg} alt="forum background" />
        </div>
      </Section>

      <Section title='Forum Posts'>
        <div className="posts-list">
          {posts.map((post) => (
            <div key={post._id} onClick={() => goToPost(post._id)} className="post">
              <h3 className="post-title">{post.title}</h3>
              <p className="post-details">
                <span className="post-author">{post.author}</span> |{' '}
                <time className="post-date">{new Date(post.createdAt).toLocaleDateString()}</time>
              </p>
            </div>
          ))}
        </div>
      </Section>

      <Section title="Recommendations for you">
        <div className="image-text-container">
          {/* Example image blocks */}
          <div className="image-block">
            <img src={recom1} alt="First Day" className="image" />
            <div className="text">First Day</div>
          </div>
          {/* Add more image blocks as needed */}
        </div>
      </Section>

      <Section title="">
        <div className="story-share">
          <h1 className='story-text1'>Share your story</h1>
          <p className='story-text2'>Do you want to keep track of your journey? Or do you prefer to connect with others who are also new in Finland? Please share your story and experiences here!</p>
          <button className="share-story-button" onClick={handleprofile}>Click here</button>
        </div>
      </Section>
    </div>
  );
}

export default Forum;
