import React, { useState } from 'react';

const PostComponent = ({ post }) => {
  const [likes, setLikes] = useState(0);
  const [comments, setComments] = useState([]);
  const [commentText, setCommentText] = useState('');

  const handleLike = () => {
    setLikes(likes + 1);
  };

  const handleCommentSubmit = (e) => {
    e.preventDefault();
    if (commentText.trim()) {
      setComments([...comments, commentText]);
      setCommentText('');
    }
  };

  return (
    <div className="post-container">
      <div className="post-header">
        <img src={`uploads/${post.filepath}`} alt={`${post.author}'s profile`} className="profile-pic" />
        <div>
          <h2 className='title'>{post.title}</h2>
          <div className='profile-info'>
            <h3 className='name'>{post.author}</h3> 
            <h3 className='location'>{post.location}</h3>
          </div>
        </div>
        <div className="tag">{post.tag}</div>
      </div>
      <div className="post-body">
        <p>{post.content}</p>
      </div>
      <div className="post-footer">
        <div className="post-stats">
          Seen {post.views} times | {new Date(post.createdAt).toLocaleDateString()}
        </div>
        <div className="post-actions">
          <button onClick={handleLike}>❤️ Like it</button>
          <span>{likes} reaction{likes !== 1 ? 's' : ''}</span>
        </div>
      </div>
      <div className="post-comments">
        {comments.map((comment, index) => (
          <p key={index}>{comment}</p>
        ))}
        <form onSubmit={handleCommentSubmit}>
          <input 
            type="text" 
            placeholder="Add a comment" 
            value={commentText} 
            onChange={(e) => setCommentText(e.target.value)} 
          />
          <button type="submit">Send</button>
        </form>
      </div>
    </div>
  );
};

export default PostComponent;
