import React, { useState } from 'react';
import './PostComponent.css'; // Import the CSS file

const PostComponent = () => {
  // Define state for likes, comments, and comment text
  const [likes, setLikes] = useState(0);
  const [comments, setComments] = useState([]);
  const [commentText, setCommentText] = useState('');

  // Function to handle liking the post
  const handleLike = () => {
    setLikes(likes + 1);
  };

  // Function to handle submitting a comment
  const handleCommentSubmit = (e) => {
    e.preventDefault();
    if (commentText.trim()) {
      setComments([...comments, commentText]);
      setCommentText('');
    }
  };

  return (
    <div className="post-container">
      <div className="post-header">
        <img
          src="profile-pic.jpg" // Replace with the actual profile picture URL
          alt="Profile Pic"
          className="profile-pic"
        />
        <div>
          <h2 className="title">Post Title</h2>
          <div className="profile-info">
            <h3 className="name">Author Name</h3>
            <h3 className="location">Author Location</h3>
          </div>
        </div>
        <div className="tag">TAG</div>
      </div>
      <div className="post-body">
        <p>
          {/* Post content goes here */}
        </p>
      </div>
      <div className="post-footer">
        <div className="post-stats">
          Seen 230 times | 11 nov. 2023
        </div>
        <div className="post-actions">
          <button onClick={handleLike}>❤️ Like it</button>
          <span>{likes} reaction{likes !== 1 ? 's' : ''}</span>
        </div>
      </div>
      <div className="post-comments">
        {comments.map((comment, index) => (
          <p key={index}>{comment}</p>
        ))}
        <form onSubmit={handleCommentSubmit}>
          <input
            type="text"
            placeholder="Add a comment"
            value={commentText}
            onChange={(e) => setCommentText(e.target.value)}
          />
          <button type="submit">Send</button>
        </form>
      </div>
    </div>
  );
};

export default PostComponent;
