import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import PostComponent from './PostComponent'; // Make sure the path is correct
import './Forum.css';
import { posts } from '../utils';  // Import your data

const Section = ({ title, children }) => {
  return (
    <section>
      <h2>{title}</h2>
      {children}
    </section>
  );
};

function Forum() {
  const [posts, setPosts] = useState([]);
  const [selectedPost, setSelectedPost] = useState(null);
  const [recommendedImages, setRecommendedImages] = useState([]); // New state for recommended images
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://localhost:3001/forum/posts')
      .then(response => {
        setPosts(response.data);
      })
      .catch(error => console.error('Error fetching posts:', error));
  }, []);

  useEffect(() => {
    // Randomly select 3 image filenames from your assets
    const assetImages = [
      'recom1.png',
      'recom2.png',
      'recom3.png',
      // Add more image filenames as needed
    ];

    // Randomly pick 3 unique image filenames
    const shuffledImages = assetImages.sort(() => 0.5 - Math.random()).slice(0, 3);
    setRecommendedImages(shuffledImages);
  }, []); // Listen for changes in posts

  const goToPost = (postId) => {
    const post = posts.find(p => p._id === postId);
    setSelectedPost(post);
  };

  const handleprofile = () => {
    navigate('/share');
  };

  const handlePostsClick = (postsName, id) => {
    console.log(`Category clicked: ${postsName}`);
    navigate(`/${id}`);
  };

  return (
    <div className="Forum">
      <Section className="Forums">
        <div className="forum-content">
          {/* your background image */}
        </div>
      </Section>

      {selectedPost ? (
        <PostComponent post={selectedPost} />
      ) : (
        <Section title='Forum Posts'>
          <div className="posts-list">
            {posts.map((post) => (
              <div key={post._id} onClick={() => goToPost(post._id)} className="post">
                <h3 className="post-title">{post.title}</h3>
                <p className="post-details">
                  <span className="post-author">{post.author}</span> |{' '}
                  <time className="post-date">{new Date(post.createdAt).toLocaleDateString()}</time>
                </p>
              </div>
            ))}
          </div>
        </Section>
      )}

      <Section title="Recommendations for you">
        <div className="image-text-container">
          {recommendedImages.map((image, index) => (
            <div className="image-block" key={index}>
              <img
                src={require(`../assets/${image}`)} // Use the appropriate image source
                alt={`Recommended Image ${index + 1}`}
                className="image"
                onClick={() => handlePostsClick(image, index)} // You can handle clicks here
              />
              <div className="text">{`Recommended Image ${index + 1}`}</div>
            </div>
          ))}
        </div>
      </Section>

      <Section>
        <div className="story-share">
          <h1 className='story-text1'>Share your story</h1>
          <p className='story-text2'>Do you want to keep track of your journey? Or do you prefer to connect with others who are also new in Finland? Please share your story and experiences here!</p>
          <button className="share-story-button" onClick={handleprofile}>Click here</button>
        </div>
      </Section>
    </div>
  );
}

export default Forum;
