import React, { useState } from 'react';
import './Forumexample.css';

const PostComponent = ({ post }) => {
  const [likes, setLikes] = useState(0);

  // Construct the image URL by combining the base URL and filepath
  const imageUrl = `${process.env.REACT_APP_SERVER_BASE_URL}/${post.filepath}`;

  const handleLike = () => {
    setLikes(likes + 1);
  };

  const handleCommentSubmit = (e) => {
    e.preventDefault();
    // ...
  };

  return (
    <div className="post-container">
      <div className="post-header">
        <img src={imageUrl} alt="Profile" className="profile-pic" />
        <div>
          <h2 className='title'>{post.title}</h2>
          <div className='profile-info'>
            <h3 className='name'>{post.author}</h3> 
            <h3 className='location'>{post.location}</h3>
          </div>
        </div>
        <div className="tag">CULTURE</div>
      </div>
      <div className="post-body">
        <p>{post.content}</p>
      </div>
      <div className="post-footer">
        <div className="post-stats">
          Seen {post.views} times | {new Date(post.createdAt).toLocaleDateString()}
        </div>
        <div className="post-actions">
          <button onClick={handleLike}>❤️ Like it</button>
          <span>{likes} reaction{likes !== 1 ? 's' : ''}</span>
        </div>
      </div>
      <div className="post-comments">
        {/* ... */}
      </div>
    </div>
  );
};

export default PostComponent;
