import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import sharebkg from '../assets/sharebkg.png';
import recom1 from '../assets/recom1.png';
import recom2 from '../assets/recom2.png';
import recom3 from '../assets/recom3.png';
import icon from '../assets/uploadicon.png';
import './Share.css';

const Section = ({ title, children }) => {
  return (
    <section>
      <h2>{title}</h2>
      {children}
    </section>
  );
};

function Share() {
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  const navigate = useNavigate();
  const [title, setTitle] = useState('');
  const [text, setText] = useState('');
  const [file, setFile] = useState(null);
  const [userProfilePicture, setUserProfilePicture] = useState(null); // New state for user's profile picture
  const fileInputRef = useRef(null);

  const handleButtonClick = () => {
    if (fileInputRef.current) {
      fileInputRef.current.click();
    }
  };

  const handleTitleChange = (event) => {
    setTitle(event.target.value);
  };

  const handleTextChange = (event) => {
    setText(event.target.value);
  };

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append('title', title);
    formData.append('content', text);
    if (file) {
      formData.append('file', file);
    }

    const token = localStorage.getItem('token');

    axios.post(`${process.env.REACT_APP_API_URL}/api/forum/posts`, formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
        'Authorization': `Bearer ${token}`
      },
    })
    .then(response => {
      console.log('Post submitted successfully', response.data);
      // Optionally reset the state or navigate to another page
    })
    .catch(error => {
      console.error('Error submitting post', error);
      // Handle error
    });
  };

  // Fetch the user's profile picture when the component mounts
  useEffect(() => {
    const fetchUserProfilePicture = async () => {
      try {
        const response = await axios.get(
          `${process.env.REACT_APP_API_URL}/api/user/profile-picture`, // Replace with your actual API endpoint
          {
            headers: {
              'Authorization': `Bearer ${token}` // Make sure to include the user's authentication token
            }
          }
        );
        setUserProfilePicture(response.data.profilePicture);
      } catch (error) {
        console.error('Error fetching user profile picture', error);
      }
    };

    fetchUserProfilePicture();
  }, []);

  const imageBlocks = [
    { src: recom1, text: 'First Day'},
    { src: recom2, text: 'New people' },
    { src: recom3, text: 'New Culture' }
  ];

  return (
    <div>
      <Section className="header">
        <div className="header-content">
          <img src={sharebkg} alt="header background"></img>
          {/* Display the user's profile picture */}
          {userProfilePicture && <img src={userProfilePicture} alt="User Profile" className="profile-picture" />}
        </div>
      </Section>

      <Section title="Share your story">
        <div className="story-share">
          <h1 className='story-text1'>Share your story</h1>
          <p className='story-text2'>Do you want to keep track of your journey? Or do you prefer to connect with others who are also new in Finland? Please share your story and experiences here!</p>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              placeholder="Title"
              value={title}
              onChange={handleTitleChange}
            />
            <textarea
              placeholder="Type here..."
              value={text}
              onChange={handleTextChange}
            />
            <div>
              <input
                type="file"
                onChange={handleFileChange}
                style={{ display: 'none' }}
                ref={fileInputRef}
              />
              <div className='button'>
                <button onClick={handleButtonClick} className="share-story-button1">
                  <img src={icon} alt="Button Icon" />
                </button>
                <button className="share-story-button2" type="submit">SEND</button>
              </div>
            </div>
          </form>
          {file && (
            <div className='filedetail'>
              <p>File name: {file.name}</p>
              <p>File type: {file.type}</p>
              <p>File size: {file.size} bytes</p>
            </div>
          )}
        </div>
      </Section>

      <Section title="Overview of your stories">
        <div className="image-text-container">
          {imageBlocks.map((block, index) => (
            <div className="image-block" key={index}>
              <img src={block.src} alt={block.text} className="image" />
              <div className="text">{block.text}</div>
            </div>
          ))}
        </div>
      </Section>
    </div>
  );
}

export default Share;
